const {DPolygon, createCanvas} = window.DGeoUtils;

const [c] = createCanvas(150, 150);
const [c2] = createCanvas(150, 150);

const p = DPolygon
  .parse([[4, 4], [10, 3], [5, 5], [5, 8], [3, 7]])
  .loop()
    .scale(10)
  .run();

const [l, r] = p.sideBuffers(5);

p.drawPolygonOnCanvas(c2, undefined, '#ff0000');
l.drawPolygonOnCanvas(c2, undefined, '#00ff00');
r.drawPolygonOnCanvas(c2, undefined, '#0000ff');

let i = 0;
const run = () => {
    const context = c.getContext('2d');
    context.clearRect(0, 0, c.width, c.height);

    p.drawPolygonOnCanvas(c, undefined, '#ff0000', undefined, undefined, (p.length - 1) / 4 * i);
    l.drawPolygonOnCanvas(c, undefined, '#00ff00', undefined, undefined, (l.length - 1) / 4 * i);
    r.drawPolygonOnCanvas(c, undefined, '#0000ff', undefined, undefined, (r.length - 1) / 4 * i);

    i = (i + 1) % 5;
    setTimeout(run, 500);
}

run();

document.body.appendChild(c);
document.body.appendChild(c2);
